// xk-base/src/main/java/com/xk/base/config/OpenApiConfig.javapackage com.xk.base.config;import io.swagger.v3.oas.models.Components;import io.swagger.v3.oas.models.OpenAPI;import io.swagger.v3.oas.models.info.Info;import io.swagger.v3.oas.models.security.SecurityRequirement;import io.swagger.v3.oas.models.security.SecurityScheme;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class OpenApiConfig {    /**     * 共用的 OpenAPI 設定：     * - 統一註冊 bearerAuth (HTTP Bearer + JWT)     * - 將 bearerAuth 設為「全域預設 security」，自動套用到所有 API     */    @Bean    public OpenAPI xkCommonOpenAPI(            @Value("${spring.application.name:xk-api}") String appName    ) {        final String securitySchemeName = "bearerAuth";        return new OpenAPI()                .info(new Info()                        .title("XK Platform - " + appName)                        .version("v1")                        .description("XK 共用 API 規格（" + appName + "）")                )                // ✅ 全域 SecurityRequirement：所有 Operation 預設都會帶 bearerAuth                .addSecurityItem(new SecurityRequirement().addList(securitySchemeName))                // ✅ 註冊 SecurityScheme：HTTP bearer + JWT                .components(new Components().addSecuritySchemes(                        securitySchemeName,                        new SecurityScheme()                                .name(securitySchemeName)                                .type(SecurityScheme.Type.HTTP)                                .scheme("bearer")                                .bearerFormat("JWT")                ));    }}