package com.xk.truck.tom.domain.model;import lombok.AllArgsConstructor;import lombok.Data;import java.time.ZonedDateTime;import java.util.UUID;@Data@AllArgsConstructorpublic class OrderStatusLog {    private UUID orderUuid;    private OrderStatus fromStatus;    private OrderStatus toStatus;    private String reason;    private ZonedDateTime createdTime;    public static OrderStatusLog created(UUID orderUuid, OrderStatus from, OrderStatus to, String reason) {        return new OrderStatusLog(orderUuid, from, to, reason, ZonedDateTime.now());    }}